#include <iostream>
#include <algorithm>
#include "../check/CheckResult.h"

using namespace std;

class Solution {
public:
    int longestOnes(vector<int>& A, int K) {
        int left = -1, right, size = A.size();
        int count = 0, maxOnesLen = 0;

        for (right = 0; right < size; ++right) {
            if (A[right] == 0) {
                if (++count > K) {
                    for (left += 1; A[left] == 1; ++left) {}
                }
            }

            maxOnesLen = max(maxOnesLen, right - left);
        }

        return maxOnesLen;
    }
};

