#include <iostream>
#include "../check/CheckResult.h"

using namespace std;

class Solution {
public:
    int numDistinct(string s, string t) {
        int i, j, sizeS = s.size(), sizeT = t.size();
        vector<vector<long long>> dp(sizeS + 1, vector<long long>(sizeT + 1, 0));  // dp[i+1][j+1]表示s[0,i]构成t[0,j]的方法数
        
        for (i = 0; i <= sizeS; ++i) {
            dp[i][0] = 1;  // t为空的方法数总是为1
        }

        for (i = 0; i < sizeS; ++i) {
            for (j = 0; j < sizeT; ++j) {
                if (t[j] == s[i]) {  // 最后一个字符匹配，两种方法构成t[0,j]：1、s[0,i-1]构成t[0,j-1]；2、s[0,i-1]构成t[0,j]
                    dp[i + 1][j + 1] = dp[i][j] + dp[i][j + 1];
                }
                else {  // 不匹配就只能s[0,i-1]构成t[0,j]
                    dp[i + 1][j + 1] = dp[i][j + 1];
                }
            }
        }

        return (int)dp[sizeS][sizeT];
    }
};

int main()
{
    CheckResult check;
    Solution o;

    check.checkInt(3, o.numDistinct("rabbbit", "rabbit"));
    check.checkInt(5, o.numDistinct("babgbag", "bag"));
    check.checkInt(1, o.numDistinct("", ""));
    check.checkInt(0, o.numDistinct("", "a"));
    check.checkInt(1, o.numDistinct("abc", ""));
    check.checkInt(543744000, o.numDistinct("", "rwmimatmhydhbujebqehjprrwfkoebcxxqfktayaaeheys"));
}

