/* 串联所有单词的子串.cpp : 此文件包含 "main" 函数。程序执行将在此处开始并结束。
给定一个字符串 s 和一些长度相同的单词 words。找出 s 中恰好可以由 words 中所有单词串联形成的子串的起始位置。

注意子串要与 words 中的单词完全匹配，中间不能有其他字符，但不需要考虑 words 中单词串联的顺序。

 

示例 1：

输入：
  s = "barfoothefoobarman",
  words = ["foo","bar"]
输出：[0,9]
解释：
从索引 0 和 9 开始的子串分别是 "barfoo" 和 "foobar" 。
输出的顺序不重要, [9,0] 也是有效答案。
示例 2：

输入：
  s = "wordgoodgoodgoodbestword",
  words = ["word","good","best","word"]
输出：[]

来源：力扣（LeetCode）
链接：https://leetcode-cn.com/problems/substring-with-concatenation-of-all-words
著作权归领扣网络所有。商业转载请联系官方授权，非商业转载请注明出处。
*/

#include <iostream>
#include <unordered_map>
#include "../check/CheckResult.h"

using namespace std;

class TrieNode {
public:
    TrieNode() {
        memset(next, 0, sizeof(TrieNode*) * 26);
        value = -1;
    }

    TrieNode* next[26];
    int value;
};

class Trie {
public:
    Trie(int len) {
        root = new TrieNode;
        wordLen = len;
    }

    void insert(string& word, int value) {
        TrieNode* current = root;

        for (int i = 0; i < wordLen; ++i) {
            int index = word[i] - 'a';
            if (current->next[index] == NULL) {
                current->next[index] = new TrieNode;
            }

            current = current->next[index];
        }

        current->value = value;
    }

    int search(string& s, int start) {
        TrieNode* current = root;

        for (int i = 0; i < wordLen; ++i) {
            int index = s[start + i] - 'a';
            if (current->next[index] == NULL) {
                return -1;
            }

            current = current->next[index];
        }

        return current->value;
    }

private:
    TrieNode* root;
    int wordLen;
};

class Solution {
public:
    vector<int> findSubstring(string s, vector<string>& words) {
        vector<string> nonDuplicateWords;
        vector<int> wordsCount;
        countWords(words, nonDuplicateWords, wordsCount);

        Trie trie(words[0].size());
        buildTrie(trie, nonDuplicateWords);

        vector<int> wordsSequence(s.size() - words[0].size() + 1);
        getAllWords(s, trie, wordsSequence);

        vector<int> positions;
        for (int start = 0; start < (int)words[0].size(); ++start) {
            findSubstr(wordsSequence, start, wordsCount, words.size(), words[0].size(), positions);
        }

        return positions;
    }

    void countWords(vector<string>& words, vector<string>& nonDuplicateWords, vector<int>& wordsCount) {
        unordered_map<string, int> tmpWords;

        for (string& word : words) {
            ++tmpWords[word];
        }

        for (auto iter = tmpWords.begin(); iter != tmpWords.end(); ++iter) {
            nonDuplicateWords.push_back(iter->first);
            wordsCount.push_back(iter->second);
        }
    }

    void buildTrie(Trie& trie, vector<string>& nonDuplicateWords) {
        for (int i = 0; i < (int)nonDuplicateWords.size(); ++i) {
            trie.insert(nonDuplicateWords[i], i);
        }
    }

    void getAllWords(string& s, Trie& trie, vector<int>& wordsSequence) {
        for (int start = 0; start < (int)wordsSequence.size(); ++start) {
            wordsSequence[start] = trie.search(s, start);
        }
    }

    void findSubstr(vector<int>& wordsSequence, int start, vector<int>& wordsCount, int wordsNum, int wordSize, vector<int>& positions) {
        int i, size = wordsSequence.size(), equals = 0;
        vector<int> count(wordsCount.size(), 0);

        for (i = start; i < size; i += wordSize) {
            int wordIndex = wordsSequence[i];
            if (wordIndex == -1) {  // 不是words中的单词，清空所有计数
                equals = 0;
                for (int& num : count) {
                    num = 0;
                }
                continue;
            }

            ++count[wordIndex];
            ++equals;
            int tmpI = i - (equals - 1) * wordSize;

            while (count[wordIndex] > wordsCount[wordIndex]) {  // 某个单词比需要的多了，从前面排除一个
                --count[wordsSequence[tmpI]];
                --equals;
                tmpI += wordSize;
            }

            if (equals == wordsNum) {  // 找到一个解
                positions.push_back(i - (wordsNum - 1) * wordSize);
                --count[wordsSequence[tmpI]];  // 计数状态回退到只差第一个单词
                --equals;
            }
        }
    }
};

