#include <iostream>
#include <algorithm>
#include "../check/CheckResult.h"

using namespace std;

class Solution {
public:
    int minDominoRotations(vector<int>& A, vector<int>& B) {
        int targetA = A[0], targetB = B[0];  // 很显然最终一样的只可能是A[0]或B[0]
        int upARotations = 0, downARotations = 0, upBRotations = 0, downBRotations = 0;
        int i, size = A.size();

        for (i = 0; i < size; ++i) {
            if (A[i] != targetA && B[i] != targetA) {
                upARotations = downARotations = INT_MAX;  // 如果A[i]、B[i]都不等于target，那么这个target不可能最终连成一排
                break;
            }

            if (A[i] != targetA && B[i] == targetA) {
                ++upARotations;
            }

            if (A[i] == targetA && B[i] != targetA) {
                ++downARotations;
            }
        }

        for (i = 0; i < size; ++i) {
            if (A[i] != targetB && B[i] != targetB) {
                upBRotations = downBRotations = INT_MAX;
                break;
            }

            if (B[i] != targetB && A[i] == targetB) {
                ++downBRotations;
            }

            if (B[i] == targetB && A[i] != targetB) {
                ++upBRotations;
            }
        }

        int rotationsA = min(upARotations, downARotations);  // 注意把A全部移到上面和全部移到下面，取小者
        int rotationsB = min(upBRotations, downBRotations);
        return (rotationsA == INT_MAX && rotationsB == INT_MAX) ? -1 : min(rotationsA, rotationsB);
    }
};

